INSERT INTO `hhailuocms_page` (`id`, `slug`, `siteid`, `title`, `content`, `template`, `inputtime`, `updatetime`) VALUES
(1, '1', 1, '首页', '<p>北京天安门</p>\r\n', 'page', '2016-02-22 07:22:18', '2016-05-23 08:11:48'),
(2, 'about-us', 1, '关于我们', '<p>欢迎来到我的&nbsp;portfolio: 在这你能看到的是一个从事在&nbsp;web programming&nbsp;&amp;&nbsp;系统架构的PHP Programer!</p>\r\n\r\n<p>E-mail: 476552238li@gmail.com</p>\r\n\r\n<h2>About</h2>\r\n\r\n<p>李志亮 . 来自湖南 . 24岁; 目前生活和工作在深圳的(Team Leader兼PHP基础架构)</p>\r\n\r\n<p>我热爱编程，并开始做一些实用的系统和工具来方便更多的像我一样的PHP Programmer.</p>\r\n\r\n<p>本科，计算机科学与技术专业，四年项目开发和面向对象编程经验，精通PHP项目开发和管理；有高并发大流量系统开发、项目管理、系统架构、SOA服务化编程经验。专业技能：</p>\r\n\r\n<ul>\r\n  <li>软件开发&amp;系统架构.</li>\r\n <li>项目管理.</li>\r\n  <li>Web服务器架构&amp;管理.</li>\r\n</ul>\r\n\r\n<h2>Portfolio</h2>\r\n\r\n<p>I hope you like my work ;)</p>\r\n\r\n<p>我主要工作是&nbsp;<strong>PHP</strong>&nbsp;服务端开发和架构。 但是我同时也掌握了&nbsp;<strong>jQuery</strong>&nbsp;前后端开发, 以及JavaScript,&nbsp;<strong>SQL</strong>,&nbsp;<strong>Ruby On Rails</strong>,&nbsp;<strong>CSS</strong>&nbsp;and&nbsp;<strong>AJAX</strong>&nbsp;等语言和技术.</p>\r\n\r\n<h2>部分作品</h2>\r\n\r\n<p>开源项目 &nbsp;<a href="http://www.tp3.hhailuo.com">TP-Admin&nbsp;</a></p>\r\n\r\n<p>创新型项目&nbsp;<a href="http://developspec.com/">DevelopSpec</a>&nbsp;（开发文档编辑工具）(Backbone + Laravel构建的全Js站点)</p>\r\n\r\n<p>大型CMS系统&nbsp;<a href="http://city.china.com.cn/">中国城市网</a>&nbsp;</p>\r\n\r\n<p>创新型项目&nbsp;<a href="http://test.city-china.com.cn/home/member/home">谁火网</a>&nbsp;</p>\r\n\r\n<p>WP+Woocommerce 构建电商网站&nbsp;<a href="http://iqair.staging.eggplanthq.com/">Iqair</a>&nbsp; &nbsp;<a href="http://goldendoor.com/">GoldenDoor</a></p>\r\n\r\n<p>WP构建多站点航班系统 &nbsp;<a href="http://www.cairnsairport.com.au/">CairnsAirport</a></p>\r\n\r\n<p>WP构建大型企业业务和企业综合系统 &nbsp;<a href="http://www.foton.deepdev.us/">Foton</a></p>\r\n\r\n<p>Android 创新项目 &nbsp;<a href="http://zhushou.360.cn/detail/index/soft_id/1588905?recrefer=SE_D_%E6%96%87%E7%90%86%E6%B4%BE">文理派&nbsp;</a></p>\r\n\r\n<p>&hellip;&hellip;&hellip;&hellip;</p>\r\n', 'page-about', '2016-02-22 07:30:07', '2016-09-19 08:57:48'),
(3, 'contact-us', 1, '联系我们', '<p>Estimates, questions, information?</p>\r\n\r\n<p>Don&#39;t hesitate to contact me.</p>\r\n\r\n<p>作者：逍遥&middot;李志亮;&nbsp;&nbsp;</p>\r\n\r\n<address>E-Mail ：&nbsp;<a href="mailto:476552238@qq.com">476552238@qq.com</a>&nbsp; &nbsp;&nbsp;<a href="https://github.com/xiaoyao-work">Github</a>&nbsp;&nbsp;</address>\r\n\r\n<address>&nbsp;</address>\r\n\r\n<h3>TP-Admin</h3>\r\n\r\n<address>反馈:&nbsp;<a href="https://github.com/xiaoyao-work/TP-Admin/issues" target="_blank">tp-admin issues</a>&nbsp;填报;&nbsp;&nbsp;</address>\r\n\r\n<p><code>QQ交流群：TP-Admin 群 号：495760597</code></p>\r\n', 'page-about', '2016-03-04 03:55:51', '2016-09-21 09:02:26');
